$(document).ready(function() {

  'use strict';

  // variables
  var form = $('.validate-form'),
    iconUploadImg = $('#icon-upload-img'),
    iconUploadBtn = $('#icon-upload'),
    logoUploadImg = $('#logo-upload-img'),
    logoUploadBtn = $('#logo-upload');

  // Update user photo on click of button
  if (iconUploadBtn) {
    iconUploadBtn.on('change', function (e) {
      var reader = new FileReader(),
        files = e.target.files;
      reader.onload = function () {
        if (iconUploadImg) {
          iconUploadImg.attr('src', reader.result);
        }
      };
      reader.readAsDataURL(files[0]);
    });
  }

   if (logoUploadBtn) {
    logoUploadBtn.on('change', function (e) {
      var reader = new FileReader(),
        files = e.target.files;
      reader.onload = function () {
        if (logoUploadImg) {
          logoUploadImg.attr('src', reader.result);
        }
      };
      reader.readAsDataURL(files[0]);
    });
  }

  

});