function openLoader(){
  $.blockUI({
      message:
        '<div class="d-flex justify-content-center align-items-center"><p class="mr-50 mb-0">Please wait...</p> <div class="spinner-grow spinner-grow-sm text-white" role="status"></div> </div>',
      css: {
        backgroundColor: 'transparent',
        color: '#fff',
        border: '0'
      },
      overlayCSS: {
        opacity: 0.5
      }
  });
}

function closeLoader(){
	$.unblockUI();
}

function blockSection(section){
  section.block({
      message: '<div class="spinner-border text-primary" role="status"></div>',
      css: {
        backgroundColor: 'transparent',
        border: '0'
      },
      overlayCSS: {
        backgroundColor: '#fff',
        opacity: 0.8
      }
  });
}

function unblockSection(section){
   section.unblock();
}

function successModal(message, titleMsg = 'success'){
   Swal.fire({
        title: titleMsg,
        text: message,
        icon: 'success',
        customClass: {
          confirmButton: 'btn btn-primary'
        },
        buttonsStyling: false
    });
}

function errorModal(message, titleMsg = 'Error!'){
    Swal.fire({
        title: titleMsg,
        text: message,
        icon: 'error',
        customClass: {
          confirmButton: 'btn btn-primary'
        },
        buttonsStyling: false
    });
}

function deleteConfirmBox(title){
  return {
        title: 'Are you sure?',
        text: "You want to delete "+ title +" data!",
        icon: 'warning',
        confirmButtonText: 'Yes, delete it!',
        showCancelButton: true,
        customClass: {
          confirmButton: 'btn btn-primary',
          cancelButton: 'btn btn-outline-danger ml-1'
        },
         buttonsStyling: false
    }
}

function areYouSure(text,confirmButtonText){
  return {
        title: 'Are you sure?',
        text: text,
        icon: 'warning',
        confirmButtonText: confirmButtonText,
        customClass: {
          confirmButton: 'btn btn-primary',
          cancelButton: 'btn btn-outline-danger ml-1'
        },
         buttonsStyling: false
    }
}
function areYouSureWithCancel(text,confirmButtonText){
  return {
        title: 'Are you sure?',
        text: text,
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: confirmButtonText,
        customClass: {
          confirmButton: 'btn btn-primary',
          cancelButton: 'btn btn-outline-danger ml-1'
        },
         buttonsStyling: false
    }
}

function safeRecord(title)
{
  Swal.fire({
      title: 'Cancelled',
      text: 'Your '+ title +' is safe :)',
      icon: 'error',
      customClass: {
        confirmButton: 'btn btn-success'
      }
    });
}

function confirmBox(){
  return {
        title: 'Are you sure?',
        text: '',
        icon: 'warning',
        confirmButtonText: 'Yes',
        customClass: {
          confirmButton: 'btn btn-primary',
          cancelButton: 'btn btn-outline-danger ml-1'
        },
         buttonsStyling: false
    }
}
