$(document).ready(function() {
	// Submit Record
    $('#frmAddEditCollection').submit(function(e) {
        e.preventDefault();
        let formData = $('#frmAddEditCollection').serialize();
        var type = "POST";
        var ajaxurl = $('#frmAddEditCollection').attr('action');
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                let res = data.response;
                if(res.status_code == 2004){
                    Swal.fire({
                        title: 'success',
                        text: res.message,
                        icon: 'success',
                        customClass: {
                          confirmButton: 'btn btn-primary'
                        },
                        buttonsStyling: false
                    });
                    // dt_basic.ajax.reload( null, false );
                } else if(res.status_code == 2006){
                    Swal.fire({
                        title: 'success',
                        text: res.message,
                        icon: 'success',
                        customClass: {
                          confirmButton: 'btn btn-primary'
                        },
                        buttonsStyling: false
                    });
                    // dt_basic.ajax.reload( null, false );
                } else {
                    Swal.fire({
                        title: 'Error!',
                        text: ' Something went wrong!',
                        icon: 'error',
                        customClass: {
                          confirmButton: 'btn btn-primary'
                        },
                        buttonsStyling: false
                    });
                }
            },
            error: function(data) {
                Swal.fire({
                    title: 'Error!',
                    text: ' Something went wrong!',
                    icon: 'error',
                    customClass: {
                      confirmButton: 'btn btn-primary'
                    },
                    buttonsStyling: false
                });
            }
        });
    });
})