$(document).ready(function() {
    var dt_filter_table = $('.dt-column-search');

    if (dt_filter_table.length) {

        var dt_basic = dt_filter_table.DataTable({
            processing: true,
            serverSide: true,
            ajax: getListingUrl,
            retrieve: true,
            aaSorting: [],
            columns: [{
                    data: 'id'
                }, // used for sorting so will hide this column
                 {
                    data: 'id',
                    name: 'id'
                },
                {
                    data: 'title',
                    name: 'title'
                },
                {
                    data: 'description',
                    name: 'description'
                },
                {
                    data: 'category',
                    name: 'category'
                },
                {
                    data: 'status',
                    name: 'status'
                },
                {
                    data: '',
                    name: displayglobalActions
                }
            ],
            columnDefs: [
                {
                  // For Checkboxes
                  targets: 0,
                  orderable: false,
                  responsivePriority: 1,
                  render: function (data, type, full, meta) {
                    return (
                      '<div class="custom-control custom-checkbox"> <input class="custom-control-input dt-checkboxes" type="checkbox" value="'+ full.id +'" id="checkbox' +
                      data +
                      '" /><label class="custom-control-label" for="checkbox' +
                      data +
                      '"></label></div>'
                    );
                  },
                  checkboxes: {
                    selectAllRender:
                      '<div class="custom-control custom-checkbox"> <input class="custom-control-input" type="checkbox" value="" id="checkboxSelectAll" /><label class="custom-control-label" for="checkboxSelectAll"></label></div>'
                  }
                },
                {
                    targets: 1,
                    responsivePriority: 1,
                    orderable: true,
                },
                {
                    targets: 2,
                    responsivePriority: 2,
                    orderable: true,
                },
                {
                    targets: 3,
                    responsivePriority: 3,
                    orderable: true,
                },
                {
                    targets: 4,
                    responsivePriority: 4,
                    orderable: true,
                },
                {
                    targets: 5,
                    responsivePriority: 5,
                    orderable: true,
                },
                {
                    // Actions
                    targets: -1,
                    title: 'Actions',
                    orderable: false,
                    searchable: false,
                    render: function(data, type, full, meta) {
                        return (
                           '<div class="d-inline-flex">' +
                            '<a href="'+ getListingUrl + '/' + full.id + '/edit' + '" class="item-edit" data-id="'+full.id+'" >' +
                            feather.icons['edit'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) +
                            '</a>' +
                            '<a href="javascript:;" class="delete-record" data-id="'+full.id+'">' +
                            feather.icons['trash-2'].toSvg({
                                class: 'font-small-4'
                            }) +
                            '</a>' +
                            '</div>'
                        );
                    }
                }
            ],
            order: [1, 'desc'],
            orderCellsTop: true,
            dom: '<"card-header border-bottom p-1"<"head-label"><"dt-action-buttons text-right"B>><"d-flex justify-content-between align-items-center mx-0 row"<"col-sm-12 col-md-6"l><"col-sm-12 col-md-6"f>>t<"d-flex justify-content-between mx-0 row"<"col-sm-12 col-md-6"i><"col-sm-12 col-md-6"p>>',
            orderCellsTop: true,
            displayLength: 10,
            lengthMenu: [10, 25, 50, 75, 100, 500, 1000],
            buttons: [
                {
                    extend: 'collection',
                    className: 'btn btn-outline-secondary dropdown-toggle mr-2',
                    text: feather.icons['share'].toSvg({
                        class: 'font-small-4 mr-50'
                    }) + 'Export',
                    buttons: [{
                            extend: 'print',
                            text: feather.icons['printer'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Print',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2]
                            }
                        },
                        {
                            extend: 'csv',
                            text: feather.icons['file-text'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Csv',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2]
                            }
                        },
                        {
                            extend: 'excel',
                            text: feather.icons['file'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Excel',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2]
                            }
                        },
                        {
                            extend: 'pdf',
                            text: feather.icons['clipboard'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Pdf',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2]
                            }
                        },
                        {
                            extend: 'copy',
                            text: feather.icons['copy'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Copy',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2]
                            }
                        }
                    ],
                    init: function(api, node, config) {
                        $(node).removeClass('btn-secondary');
                        $(node).parent().removeClass('btn-group');
                        setTimeout(function() {
                            $(node).closest('.dt-buttons').removeClass('btn-group').addClass('d-inline-flex');
                        }, 50);
                    }
                },
                {
                    text: feather.icons['plus'].toSvg({
                        class: 'mr-50 font-small-4'
                    }) + 'Add New Notification',
                    className: 'create-new btn btn-primary',
                    
                    init: function(api, node, config) {
                        $(node).removeClass('btn-secondary');
                    }
                }
            ],
            language: {
                paginate: {
                    // remove previous & next text from pagination
                    previous: '&nbsp;',
                    next: '&nbsp;'
                }
            },
            initComplete: function(){
                $("#DataTables_Table_0 tbody").after('<tfoot></tfoot>');
                $("#DataTables_Table_0 thead tr:first").clone().appendTo($("#column-search-datatable tfoot"));
            }
        });

        $('div.head-label').html('<h6 class="mb-0">Notification Listing</h6>');

        $('.dataTable thead').on('input select', '.search', function () {
              let strict = $(this).attr('strict') || false
              let value = strict && this.value ? "^" + this.value + "$" : this.value
              dt_basic
                .column($(this).parent().index())
                .search(value, strict)
                .draw()
        });
    }

    $(document).on('click', '.create-new', function(){
        window.location = createUrl;
    });
    // Delete Record
    $('.dt-column-search tbody').on('click', '.delete-record', function(e) {
        // dt_basic.row($(this).parents('tr')).remove().draw();
        let id = jQuery(this).data('id');
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            customClass: {
              confirmButton: 'btn btn-primary',
              cancelButton: 'btn btn-outline-danger ml-1'
            },
             buttonsStyling: false
        }).then(function (result) {
            if (result.value) {
              deleteRecord(id);
            } else if (result.dismiss === Swal.DismissReason.cancel) {
              Swal.fire({
                title: 'Cancelled',
                text: 'Your theme is safe :)',
                icon: 'error',
                customClass: {
                  confirmButton: 'btn btn-success'
                }
              });
            }
        });
    });

    // Delete Record function
    function deleteRecord(id)
    {
        openLoader();
        var formData = {
        };
        var type = "POST";
        var ajaxurl = deleteUrl + '/'+ id;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code == 3022){
                    Swal.fire({
                        icon: 'success',
                        title: 'Deleted!',
                        text: res.message,
                        customClass: {
                          confirmButton: 'btn btn-success'
                        }
                    });
                    $('.modal').modal('hide');
                    dt_basic.ajax.reload( null, false );
                } else {
                    Swal.fire({
                        title: 'Error!',
                        text: ' Something went wrong!',
                        icon: 'error',
                        customClass: {
                          confirmButton: 'btn btn-primary'
                        },
                        buttonsStyling: false
                    });
                }
            },
            error: function(data) {
                closeLoader();
                Swal.fire({
                    title: 'Error!',
                    text: ' Something went wrong!',
                    icon: 'error',
                    customClass: {
                      confirmButton: 'btn btn-primary'
                    },
                    buttonsStyling: false
                });
            }
        });
    }

    $(document).on('change', '#checkboxSelectAll, .dt-checkboxes', function(){
        if($('#checkboxSelectAll').prop('checked')) {
            if($('.card-header').find('.btn-more-actions').length == 0 || typeof $('.card-header').find('.btn-more-actions').length === "undefined"){
                dt_basic.button().add( 0, {
                    extend: 'collection',
                    className: 'btn btn-outline-secondary dropdown-toggle mr-2 btn-more-actions',
                    text:  'More Actions',
                    buttons: [{
                            text: feather.icons['trash'].toSvg({
                            class: 'font-small-4 mr-50'
                        }) + 'Delete Selected',
                            className: 'dropdown-item delete-selected',
                        },
                    ],
                    init: function(api, node, config) {
                        $(node).removeClass('btn-secondary');
                        $(node).parent().removeClass('btn-group');
                        setTimeout(function() {
                            $(node).closest('.dt-buttons').removeClass('btn-group').addClass('d-inline-flex');
                        }, 50);
                    }
                });
            }

        } else {
            dt_basic.buttons( '.btn-more-actions' ).remove();
        }
    });

    $(document).on('click', '.delete-selected', function(){

        Swal.fire({
            title: 'Are you sure?',
            text: "You want to delete all selected recoreds!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            customClass: {
              confirmButton: 'btn btn-primary',
              cancelButton: 'btn btn-outline-danger ml-1'
            },
             buttonsStyling: false
        }).then(function (result) {
            if (result.value) {
                deleteSelectedRecord();
            } else if (result.dismiss === Swal.DismissReason.cancel) {
              Swal.fire({
                title: 'Cancelled',
                text: 'Your vendor is safe :)',
                icon: 'error',
                customClass: {
                  confirmButton: 'btn btn-success'
                }
              });
            }
        });
    });

    // Delete Record function
    function deleteSelectedRecord()
    {
        openLoader();
        let ids = [];
        $('.dt-checkboxes').each(function () {
            if($(this).prop('checked')){
                ids.push($(this).val());
            }
        });

        var formData = {
            ids: ids
        };
        var type = "POST";
        var ajaxurl = destroyUrl;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code == 3022){
                    Swal.fire({
                        icon: 'success',
                        title: 'Deleted!',
                        text: res.message,
                        customClass: {
                          confirmButton: 'btn btn-success'
                        }
                    });
                    $('.modal').modal('hide');
                    dt_basic.buttons( '.btn-more-actions' ).remove();
                    dt_basic.ajax.reload( null, false );
                } else {
                    Swal.fire({
                        title: 'Error!',
                        text: ' Something went wrong!',
                        icon: 'error',
                        customClass: {
                          confirmButton: 'btn btn-primary'
                        },
                        buttonsStyling: false
                    });
                }
            },
            error: function(data) {
                closeLoader();
                Swal.fire({
                    title: 'Error!',
                    text: ' Something went wrong!',
                    icon: 'error',
                    customClass: {
                      confirmButton: 'btn btn-primary'
                    },
                    buttonsStyling: false
                });
            }
        });
    }
});