 // Delete Record
    $('.dt-column-search tbody').on('click', '.delete-record', function(e) {
        // dt_filter_table.row($(this).parents('tr')).remove().draw();
        let id = jQuery(this).data('id');
      Swal.fire(deleteConfirmBox('orders')).then(function (result) {
            if (result.value) {
              deleteRecord(id);
            } else if (result.dismiss === Swal.DismissReason.cancel) {
              safeRecord('orders');
            }
        });
    });

    // Delete Record function
    function deleteRecord(id)
    {
         openLoader();
        var formData = {
            "_method": 'DELETE',
        };
        var type = "POST";
        var ajaxurl = getListingUrl + '/'+ id;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code == 3086){
                    successModal(res.message, 'Deleted!');
                    $('.modal').modal('hide');
                    dt_basic.ajax.reload();
                } else {
                     errorModal('Something went wrong!');
                }
            },
            error: function(data) {
                closeLoader();
                 errorModal('Something went wrong!');
            }
        });
    }

