$(document).ready(function() {
    var dt_filter_table = $('.dt-column-search');

    if (dt_filter_table.length) {

        var dt_filter_table = dt_filter_table.DataTable({
            processing: true,
            serverSide: true,
            ajax: getListingUrl,
            columns: [{
                    data: 'id'
                }, // used for sorting so will hide this column
                {
                    data: 'id'
                }, // used for sorting so will hide this column
                {
                    data: 'filter',
                    name: 'filter'
                },
                {
                    data: 'description',
                    name: 'description'
                },
                {
                    data: 'status',
                    name: 'status'
                },
                {
                    data: 'shipping_method_id',
                    name: 'shipping_methods.title'
                },
                {
                    data: '',
                    name: displayglobalActions
                }
            ],
            columnDefs: [
                {
                  // For Checkboxes
                  targets: 0,
                  orderable: false,
                  responsivePriority: 1,
                  searchable: false,
                  render: function (data, type, full, meta) {
                    return (
                      '<div class="custom-control custom-checkbox"> <input class="custom-control-input dt-checkboxes" type="checkbox" value="'+ full.id +'" id="checkbox' +
                      data +
                      '" /><label class="custom-control-label" for="checkbox' +
                      data +
                      '"></label></div>'
                    );
                  },
                  checkboxes: {
                    selectAllRender:
                      '<div class="custom-control custom-checkbox"> <input class="custom-control-input" type="checkbox" value="" id="checkboxSelectAll" /><label class="custom-control-label" for="checkboxSelectAll"></label></div>'
                  }
                },
                {
                    targets: 1,
                    responsivePriority: 2,
                },
                {
                    targets: 2,
                    responsivePriority: 3,
                },
                {
                    targets: 3,
                    responsivePriority: 4,
                },
                {
                    targets: 4,
                    responsivePriority: 4,
                },
                {
                    targets: 5,
                    responsivePriority: 5,
                },
                {
                    // Actions
                    targets: -1,
                    title: 'Actions',
                    orderable: false,
                    searchable: false,
                    render: function(data, type, full, meta) {
                        return (
                            '<div class="d-inline-flex">' +
                            '<a href="javascript:;" class="item-edit edit_btn" data-id="'+full.id+'" >' +
                            feather.icons['edit'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) +
                            '</a>' +
                            '<a href="javascript:;" class="delete-record delete_btn" data-id="'+full.id+'">' +
                            feather.icons['trash-2'].toSvg({
                                class: 'font-small-4'
                            }) +
                            '</a>' +
                            '</div>'
                        );
                    }
                }
            ],
            order: [
                [1, 'dese']
            ],
            dom: '<"card-header border-bottom p-1"<"head-label"><"dt-action-buttons text-right"B>><"d-flex justify-content-between align-items-center mx-0 row"<"col-sm-12 col-md-6"l><"col-sm-12 col-md-6"f>>t<"d-flex justify-content-between mx-0 row"<"col-sm-12 col-md-6"i><"col-sm-12 col-md-6"p>>',
            orderCellsTop: true,
            displayLength: 10,
            lengthMenu: [10, 25, 50, 75, 100,200,400,500,1000,1500,2000],
            buttons: [
                {
                    extend: 'collection',
                    className: 'btn btn-outline-secondary dropdown-toggle mr-2 btn-export-access',
                    attr: {
                        'id': 'exportbutton'
                    },
                    text: feather.icons['share'].toSvg({
                        class: 'font-small-4 mr-50'
                    }) + 'Export',
                    buttons: [{
                            extend: 'print',
                            text: feather.icons['printer'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Print',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2, 3, 4]
                            }
                        },
                        {
                            extend: 'csv',
                            text: feather.icons['file-text'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Csv',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2, 3, 4]
                            }
                        },
                        {
                            extend: 'excel',
                            text: feather.icons['file'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Excel',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2, 3, 4]
                            }
                        },
                        {
                            extend: 'pdf',
                            text: feather.icons['clipboard'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Pdf',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2, 3, 4]
                            }
                        },
                        {
                            extend: 'copy',
                            text: feather.icons['copy'].toSvg({
                                class: 'font-small-4 mr-50'
                            }) + 'Copy',
                            className: 'dropdown-item',
                            exportOptions: {
                                columns: [1, 2, 3, 4]
                            }
                        }
                    ],
                    init: function(api, node, config) {
                        $(node).removeClass('btn-secondary');
                        $(node).parent().removeClass('btn-group');
                        setTimeout(function() {
                            $(node).closest('.dt-buttons').removeClass('btn-group').addClass('d-inline-flex');
                        }, 50);
                    }
                },
                {
                    text: feather.icons['plus'].toSvg({
                        class: 'mr-50 font-small-4'
                    }) + 'Add New Record',
                    className: 'create-new btn btn-primary btn-create-access',
                    attr: {
                        'data-toggle': 'modal',
                        'data-target': '#modals-slide-in',
                        'id': 'btnCreate'
                    },
                    init: function(api, node, config) {
                        $(node).removeClass('btn-secondary');
                    }
                }
            ],
            language: {
                paginate: {
                    // remove previous & next text from pagination
                    previous: '&nbsp;',
                    next: '&nbsp;'
                }
            },
            initComplete: function(){

                if($("#ship_orders_create").length != 1)
                {
                    $("#btnCreate").remove();
                }

                if($("#ship_orders_edit").length != 1)
                {
                    $(".edit_btn").remove();
                }
                if($("#ship_orders_delete").length != 1)
                {
                    $(".delete_btn").remove();
                }
                if($("#ship_orders_export").length != 1)
                {
                    $("#exportbutton").remove();
                }
                $("#DataTables_Table_0 tbody").after('<tfoot></tfoot>');
                $("#DataTables_Table_0 thead tr:first").clone().appendTo($("#column-search-datatable tfoot"));
            },
        });
        $('div.head-label').html('<h6 class="mb-0">Ship Orders Listing</h6>');

        $('.dataTable thead').on('input select', '.search', function () {
              let strict = $(this).attr('strict') || false
              let value = strict && this.value ? "^" + this.value + "$" : this.value
              dt_filter_table
                .column($(this).parent().index())
                .search(value, strict)
                .draw()
        });
    }

    // Submit Record
    $('.data-submit').on('click', function(e) {
        openLoader();
        e.preventDefault();
        var formData = {
            filter: jQuery('#addFilter').val(),
            description: jQuery('#addDescription').val(),
            status: jQuery('#addStatus').val(),
            shipping_method_id: jQuery('#shipping_method_id').val()
        };
        var type = "POST";
        var ajaxurl = storeUrl;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code == 3069){
                    successModal(res.message);
                    $('.modal').modal('hide');
                    dt_filter_table.ajax.reload( null, false );
                } else {
                    errorModal('Something went wrong!');
                }
            },
            error: function(data) {
                closeLoader();
                errorModal('Something went wrong!');
            }
        });
    });

    // Delete Record
    $('.dt-column-search tbody').on('click', '.delete-record', function(e) {
        // dt_filter_table.row($(this).parents('tr')).remove().draw();
        let id = jQuery(this).data('id');
      Swal.fire(deleteConfirmBox('shiporders')).then(function (result) {
            if (result.value) {
              deleteRecord(id);
            } else if (result.dismiss === Swal.DismissReason.cancel) {
              safeRecord('shiporders');
            }
        });
    });

    // Delete Record function
    function deleteRecord(id)
    {
         openLoader();
        var formData = {
            "_method": 'DELETE',
        };
        var type = "POST";
        var ajaxurl = getListingUrl + '/'+ id;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code ==  3070){
                    successModal(res.message, 'Deleted!');
                    $('.modal').modal('hide');
                    dt_filter_table.ajax.reload();
                } else {
                     errorModal('Something went wrong!');
                }
            },
            error: function(data) {
                closeLoader();
                 errorModal('Something went wrong!');
            }
        });
    }

    $(document).on('change', '#checkboxSelectAll, .dt-checkboxes', function(){
        if($('#checkboxSelectAll').prop('checked')) {
            if($('.card-header').find('.btn-more-actions').length == 0 || typeof $('.card-header').find('.btn-more-actions').length === "undefined"){
                dt_filter_table.button().add( 0, {
                    extend: 'collection',
                    className: 'btn btn-outline-secondary dropdown-toggle mr-2 btn-more-actions',
                    text:  'More Actions',
                    buttons: [{
                            text: feather.icons['trash'].toSvg({
                            class: 'font-small-4 mr-50'
                        }) + 'Delete Selected',
                            className: 'dropdown-item delete-selected',
                        },
                    ],
                    init: function(api, node, config) {
                        $(node).removeClass('btn-secondary');
                        $(node).parent().removeClass('btn-group');
                        setTimeout(function() {
                            $(node).closest('.dt-buttons').removeClass('btn-group').addClass('d-inline-flex');
                        }, 50);
                    }
                });
            }

        } else {
            dt_filter_table.buttons( '.btn-more-actions' ).remove();
        }
    });

    $(document).on('click', '.delete-selected', function(){
        Swal.fire(deleteConfirmBox('shiporders')).then(function (result) {
            console.log(result.dismiss, Swal.DismissReason.cancel);
            if (result.value) {
                deleteSelectedRecord();
            } else if (result.dismiss === Swal.DismissReason.cancel) {
              safeRecord('all selected shiporders');
            }
        });
    });

    // Delete Record function
    function deleteSelectedRecord()
    {
        openLoader();
        let ids = [];
        $('.dt-checkboxes').each(function () {
            if($(this).prop('checked')){
                ids.push($(this).val());
            }
        });
        var formData = {
            "_method": 'DELETE',
            ids: ids
        };
        var type = "POST";
        var ajaxurl = deleteUrl;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code == 3070){
                     successModal(res.message, 'Deleted!');
                    $('.modal').modal('hide');
                    dt_filter_table.buttons( '.btn-more-actions' ).remove();
                    dt_filter_table.ajax.reload();
                } else {
                     errorModal('Something went wrong!');
                }
            },
            error: function(data) {
                closeLoader();
                errorModal('Something went wrong!');
            }
        });
    }

    $(document).on('click', '.item-edit', function(){
        //get current row data
        var tr = $(this).closest("tr");
        var rowindex = tr.index();

        let data = dt_filter_table.row(rowindex).data();

        $('#shipOrdersId').val(data.id);
        $('#editFilter').val(data.filter);
        $('#editDescription').val(data.description);
        let status = data.status == 'Active' ? 1 : 0;
        $('#editStatus').val(status);
        $('#shippingMethodId').val(data.shipping_method_id);
        $('#default').modal('show');
    });

    // Edit Record
    $(document).on('click', '.data-edit', function(){
        openLoader();
        let id = $('#shipOrdersId').val(),
        filter = $('#editFilter').val(),
        description = $('#editDescription').val(),
        status = $('#editStatus').val(),
        shipping_method_id = $('#shippingMethodId').val();
        
        var formData = {
            "_method": 'PUT',
            filter: filter,
            description: description,
            status: status,
            shipping_method_id: shipping_method_id
        };
        var type = "POST";
        var ajaxurl = getListingUrl + '/'+ id;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                closeLoader();
                let res = data.response;
                if(res.status_code == 3071){
                    successModal(res.message, 'Edited!');
                    $('.modal').modal('hide');
                    dt_filter_table.ajax.reload();
                } else {
                     errorModal('Something went wrong!');
                }
            },
            error: function(data) {
                closeLoader();
                errorModal('Something went wrong!');
            }
        });
    });

});