    // Delete Record function
    function deleteRecord(id)
    {
        openLoader();
        var formData = {
            "_method": 'DELETE',
        };
        var type = "POST";
        var ajaxurl = getListingUrl + '/delete/'+ id;
        $.ajax({
            type: type,
            url: ajaxurl,
            data: formData,
            dataType: 'json',
            success: function(data) {
                let res = data.response;
                if(res.status_code == 1012){
                    successModal(res.message, 'Deleted!');
                    $('.modal').modal('hide');
                    dt_basic.ajax.reload( null, false );
                } else if(res.status_code == 1013){ 
                    errorModal(err.response.message);
                } else {
                    errorModal('Something went wrong!');
                }
                closeLoader();
            },
            error: function(data) {
               closeLoader();
               errorModal('Something went wrong!');
            }
        });
    }

