// Regenerate record function
function regenerateXMLFile(id)
{
    openLoader();
    var type = "GET";
    var ajaxurl = "/api/admin/xmlfeed/regenerate-xml-file/"+id;
    $.ajax({
        type: type,
        url: ajaxurl,
        dataType: 'json',
        success: function(data) {
             let res = data.response;
            if(res.status_code == 3002){
                successModal(res.message, 'Success');
            } else {
                errorModal('Something went wrong!');
            }
            closeLoader();
        },
        error: function(data) {
            errorModal('Something went wrong!');
            closeLoader();
        }
    });
}